import React, {useState} from "react";
import {nanoid} from "nanoid";
import TodoItem from "./TodoItem";

function App() {
  const [inputText, setInputText] = useState("");
  const [items, setItems] = useState([]);
  const [toggleBtn, setToggleBtn] = useState(false);

  function handleChange(event) {
    const newValue = event.target.value;
    setInputText(newValue);
  }

  function addItem() {
    const newItem = {id: "todo-" + nanoid() ,name:inputText ,newName:"", completed: false};
    setItems(prevItems => {
      return [...prevItems, newItem];
    });
    console.log(newItem);
    setInputText("");
  }

  function deleteItem(id) {
    setItems((prevItems) => {
      return prevItems.filter((item, index) => {
        return item.id !== id;
      });
    });
  }
  
  function toggleTaskCompleted(id) {
    setItems((prevItems) => {
      return prevItems.map((item, index) => {
        if(item.id === id)
          return Object.assign({},item, {completed: !item.completed})
        else 
          return item
      });
    });
  }

  function changeTodoItem(id) {
    setToggleBtn((prevValue) => {
      if(prevValue === "Add")
          return "Edit"
      else
          return "Add"
    });

    // setItems((prevItems) => {
    //   return prevItems.map((item, index) => {
    //     if(item.id === id)
    //       return Object.assign({},item, {name: item.newName})
    //     else 
    //       return item
    //   });
    // });
  }

  return (
    <div className="container">
      <div className="heading">
        <h1>To-Do List</h1>
      </div>
      <div className="form">
        <input onChange={handleChange} type="text" value={inputText}  autoFocus/>
        <button onClick={addItem}>
          <span>{toggleBtn}</span>
        </button>
      </div>
      <div>
        <ul>
          {items.map((todoItem, index) => (
            <TodoItem
                key = {index}
                id= {todoItem.id}
                name= {todoItem.name}
                todoList = {todoItem}
                onChecked= {deleteItem}
                isDone= {todoItem.completed}
                toggleTaskCompleted= {toggleTaskCompleted}
                changeTodoItem= {changeTodoItem}
               />
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
