import React, {useState} from "react";

function TodoItem(props) {
    // const [isDone, setCheck] = useState(false);
    
    // function toggleTaskCompleted() {
    //     setCheck((prevValue) => {
    //         prevValue.find(o => o.id === )
    //     })
    // }
    return (
        <div  className="to-do-text" style={{textDecoration: props.isDone ? "line-through": "none"}} >
            <li > {props.name} </li>
            <div>
               <i className="fas fa-trash-alt" 
                    onClick= {() => {
                        props.onChecked(props.id);
                    }}
                ></i>
            </div>
        </div>
    );
}

export default TodoItem;
