import React, {useState} from "react";

function TodoItem(props) {
    const [isDone, setCheck] = useState(false);
    
    function handleClick() {
        setCheck((prevValue) => {
            return !prevValue;
        })
    }
    return (
        <div className="to-do-text" style={{textDecoration: isDone ? "line-through": "none"}} >
            <li onClick={handleClick} > {props.todoList} </li>
            <div>
               <i className="fas fa-trash-alt" 
                    onClick= {() => {
                        props.onChecked(props.key);
                    }}
                ></i>
            </div>
        </div>
    );
}

export default TodoItem;
